// Business Value Metrics
let fileUploads = customEvents | where timestamp > ago(30d) and name == 'CSVFileUpload' | extend fileSize = todouble(customMeasurements.fileSize), rowCount = todouble(customMeasurements.rowCount);
let chatInteractions = customEvents | where timestamp > ago(30d) and name == 'CSVChatInteraction';
let userActivity = customEvents | where timestamp > ago(30d) and name in ('FileUpload', 'ChatInteraction') | extend userId = tostring(customDimensions.userId) | where isnotempty(userId) and userId != 'anonymous';
union
    (fileUploads | summarize Value = round(sum(fileSize) / 1024 / 1024 / 1024, 2) | extend Metric = 'Total Data Processed (GB)', Category = 'Data Volume'),
    (fileUploads | summarize Value = round(sum(rowCount) / 1000000, 2) | extend Metric = 'Total Rows Processed (Millions)', Category = 'Data Volume'),
    (chatInteractions | summarize Value = count() | extend Metric = 'Total Chat Interactions', Category = 'User Engagement'),
    (userActivity | summarize Value = dcount(tostring(customDimensions.userId)) | extend Metric = 'Monthly Active Users', Category = 'User Engagement'),
    (fileUploads | summarize Value = count() | extend Metric = 'Files Processed', Category = 'Processing Volume'),
    (userActivity | summarize Value = dcount(strcat(tostring(customDimensions.userId), '_', bin(timestamp, 1d))) | extend Metric = 'User-Days of Activity', Category = 'User Engagement')
| order by Category, Metric

// User Retention Analysis
let users_week1 = customEvents | where timestamp between (ago(14d) .. ago(7d)) | where name in ('FileUpload', 'ChatInteraction') | extend userId = tostring(customDimensions.userId) | where isnotempty(userId) and userId != 'anonymous' | distinct userId;
let users_week2 = customEvents | where timestamp > ago(7d) | where name in ('FileUpload', 'ChatInteraction') | extend userId = tostring(customDimensions.userId) | where isnotempty(userId) and userId != 'anonymous' | distinct userId;
let new_users = users_week2 | join kind=leftanti users_week1 on userId;
let retained_users = users_week2 | join kind=inner users_week1 on userId;
let churned_users = users_week1 | join kind=leftanti users_week2 on userId;
union
    (users_week1 | summarize Count = count() | extend UserType = 'Week 1 Users'),
    (users_week2 | summarize Count = count() | extend UserType = 'Week 2 Users'),
    (new_users | summarize Count = count() | extend UserType = 'New Users'),
    (retained_users | summarize Count = count() | extend UserType = 'Retained Users'),
    (churned_users | summarize Count = count() | extend UserType = 'Churned Users')
| extend RetentionRate = case(
    UserType == 'Retained Users', round((Count / toscalar(users_week1 | count())) * 100, 1),
    UserType == 'New Users', round((Count / toscalar(users_week2 | count())) * 100, 1),
    0.0
)
| project UserType, Count, RetentionRate
| order by case(UserType == 'Week 1 Users', 1, UserType == 'Week 2 Users', 2, UserType == 'Retained Users', 3, UserType == 'New Users', 4, 5)
